import React, { useContext } from 'react';
import App, { AppContext } from '../App';
import { useState, useEffect } from 'react';

const PokemonDisplay = () => {
  const {currentPokemon} = useContext(AppContext);
  const {pokeID} = useContext(AppContext);
  const {afterImg} = useContext(AppContext);
  const {resetGame} = useContext(AppContext);

  if(resetGame){
    const imgSrc =  '/assets/1_before.png';
  }else if(afterImg){
    imgSrc = `/assets/${pokeID}_after.png`;
  }else{
    `/assets/${pokeID}_before.png`
  }


  return (
    <div>
      src={imageSrc}

    </div>
  )
};

export default PokemonDisplay;
